angular
  .module('app', [
    'ngRoute',
    'moduleName'
  ]);


angular
    .module('moduleName', []);

angular
  .module('moduleName')
  .controller('ModuleController', ModuleController);

function ModuleController() {
  var vm = this;

  vm.name = 'Programmer';
  vm.setName = function(name) {
    vm.name = name;
  };

};


angular
  .module('app')
  .config(config);

function config($routeProvider) {
  $routeProvider
    .when('/', {
          templateUrl: 'js/app/modules/base/home.html'
      })
      .when('/mod', {
          templateUrl: 'js/app/modules/module/module.html',
          controller: 'ModuleController',
          controllerAs: 'vm'
      });
}